mapper.insert:
SELECT SEQ_COUNTER_UPLOAD_LOG.CURRVAL FROM DUAL
insert into ${tableName} (
) values ( SEQ_COUNTER_UPLOAD_LOG.NEXTVAL, #{imei,jdbcType=VARCHAR}, #{ua,jdbcType=VARCHAR}, #{modelName,jdbcType=VARCHAR}, #{channelId,jdbcType=VARCHAR}, #{deviceCode,jdbcType=VARCHAR}, #{groupId,jdbcType=BIGINT}, #{batchCode,jdbcType=VARCHAR}, #{processTime,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, #{active,jdbcType=VARCHAR} )

mapper.select:




select
*
from (
        select t2.*,rownum rn from ${tableName} t2
                 where t2.create_time >= #{startDate,jdbcType=TIMESTAMP}
                    and t2.create_time< #{endDate,jdbcType=TIMESTAMP}
) t where rn> #{startNum} and rn <= #{endNum}

select t.MODEL_NAME,ci.CHANNEL_NAME,t.create_time,t.imei
from ${tableName} t LEFT OUTER JOIN TY_CHANNEL_INFO ci on t.CHANNEL_ID = ci.CHANNEL_ID
where 1=1

    and t.GROUP_ID = #{groupId,jdbcType=BIGINT}


    and t.MODEL_NAME = #{modleName,jdbcType=VARCHAR}


    and t.PROCESS_TIME = #{processTime}


      and  t.CREATE_TIME >= #{createTime,jdbcType=TIMESTAMP}  


    and t.CHANNEL_ID = #{channelId,jdbcType=BIGINT}


    and t.active in

        #{item}

select t.MODEL_NAME,ci.CHANNEL_NAME,t.create_time,t.imei
from ${tableName} t LEFT OUTER JOIN TY_CHANNEL_INFO ci on t.CHANNEL_ID = ci.CHANNEL_ID
where 1=1

    and t.GROUP_ID = #{groupId,jdbcType=BIGINT}


    and t.MODEL_NAME = #{modleName,jdbcType=VARCHAR}


    and t.PROCESS_TIME = #{processTime}


      and  t.CREATE_TIME >= #{createTime,jdbcType=TIMESTAMP}  


    and t.CHANNEL_ID = #{channelId,jdbcType=BIGINT}


    and t.active in

        #{item}

select t.MODEL_NAME,ci.CHANNEL_NAME,t.create_time,t.imei
from ${tableName} t LEFT OUTER JOIN TY_CHANNEL_INFO ci on t.CHANNEL_ID = ci.CHANNEL_ID
where 1=1

    and t.GROUP_ID = #{groupId,jdbcType=BIGINT}


    and t.MODEL_NAME = #{modleName,jdbcType=VARCHAR}


    AND trunc(t.CREATE_TIME,'DD')=TO_DATE(#{createTime},'YYYY-MM-DD')


    and t.CHANNEL_ID = #{channelId,jdbcType=BIGINT}
