mapper.insert:
SELECT SEQ_DATA_LOG.CURRVAL FROM DUAL
insert into ${tableName} (
) values ( SEQ_DATA_LOG.NEXTVAL, #{imei,jdbcType=VARCHAR}, #{ua,jdbcType=VARCHAR}, #{channelId,jdbcType=VARCHAR}, #{deviceCode,jdbcType=VARCHAR}, #{groupId,jdbcType=BIGINT}, #{batchCode,jdbcType=VARCHAR}, #{processTime,jdbcType=DATE}, #{deviceUploadTime,jdbcType=TIMESTAMP}, #{active,jdbcType=INTEGER}, #{counterUploadTime,jdbcType=TIMESTAMP} )

mapper.update:
update ${tableName}
set ACTIVE = #{active},
COUNTER_UPLOAD_TIME = #{counterUploadTime}
where ID = #{id}

mapper.select:
select
count(0)
from ${tableName}
where DEVICE_UPLOAD_TIME between #{uploadStartTime} and #{uploadEndTime}
and UA = #{ua}
and CHANNEL_ID = #{channelId}
and DEVICE_CODE = #{deviceCode}

select
count(0)
from ${tableName} log
where exists (select ref.BATCH_CODE from TY_BATCH_PRODUCT_REF ref where ref.PRODUCT_ID = #{productId} and
ref.BATCH_CODE = log.BATCH_CODE)
and log.DEVICE_UPLOAD_TIME between #{uploadStartTime} and #{uploadEndTime}
and log.UA = #{ua}
and log.GROUP_ID = #{groupId}

select
count(0)
from ${tableName}
where DEVICE_UPLOAD_TIME between #{uploadStartTime} and #{uploadEndTime}

select

from ${dataLogRequest.tableName}
where DEVICE_UPLOAD_TIME between #{dataLogRequest.uploadStartTime} and #{dataLogRequest.uploadEndTime}

select IMEI
from ${tableName}
where UA = #{ua}

    and CHANNEL_ID = #{channelId}


    and DEVICE_CODE = #{deviceCode}



        and PROCESS_TIME between #{startTime} and #{endTime}


        and DEVICE_UPLOAD_TIME between #{startTime} and #{endTime}


        and PROCESS_TIME between #{startTime} and #{endTime}



                    and ACTIVE != 0


                    and ACTIVE = 1


                    and ACTIVE in (2,3,4)


                    and ACTIVE in (2,4)


                    and ACTIVE in (3,4)

select log.IMEI
from ${tableName} log
where exists (select ref.BATCH_CODE from TY_BATCH_PRODUCT_REF ref where ref.PRODUCT_ID = #{productId} and
ref.BATCH_CODE = log.BATCH_CODE)
and log.UA = #{ua}
and log.GROUP_ID = #{groupId}


        and log.PROCESS_TIME between #{startTime} and #{endTime}


        and log.DEVICE_UPLOAD_TIME between #{startTime} and #{endTime}


        and log.PROCESS_TIME between #{startTime} and #{endTime}



                    and log.ACTIVE != 0


                    and log.ACTIVE = 1


                    and log.ACTIVE in (2,3,4)


                    and log.ACTIVE in (2,4)


                    and log.ACTIVE in (3,4)
