) values ( SEQ_DATA_LOG.NEXTVAL, #{imei,jdbcType=VARCHAR}, #{ua,jdbcType=VARCHAR}, #{modelName,jdbcType=VARCHAR}, #{channelId,jdbcType=VARCHAR}, #{deviceCode,jdbcType=VARCHAR}, #{groupId,jdbcType=BIGINT}, #{batchCode,jdbcType=VARCHAR}, #{processTime,jdbcType=DATE}, #{deviceUploadTime,jdbcType=TIMESTAMP}, #{active,jdbcType=INTEGER} #{counterUploadTime,jdbcType=TIMESTAMP}, #{md5Key,jdbcType=VARCHAR} )

