mapper.insert:
SELECT SEQ_DATA_LOG.CURRVAL FROM DUAL
insert into ${tableName} (
) values ( SEQ_DATA_LOG.NEXTVAL, #{imei,jdbcType=VARCHAR}, #{ua,jdbcType=VARCHAR}, #{channelId,jdbcType=VARCHAR}, #{deviceCode,jdbcType=VARCHAR}, #{groupId,jdbcType=BIGINT}, #{batchCode,jdbcType=VARCHAR}, #{processTime,jdbcType=DATE}, #{deviceUploadTime,jdbcType=TIMESTAMP}, #{active,jdbcType=INTEGER}, #{counterUploadTime,jdbcType=TIMESTAMP}, #{md5Key,jdbcType=VARCHAR}, #{pmd5Key,jdbcType=VARCHAR} )

mapper.update:
update ${tableName}
set ACTIVE = #{active},
COUNTER_UPLOAD_TIME = #{counterUploadTime},
P_MD5_KEY = #{pmd5Key}

where ID = #{id}

mapper.select:
select
count(0)
from ${tableName}
where DEVICE_UPLOAD_TIME between #{startTime} and #{endTime}



            and P_MD5_KEY = #{md5Key}


            and MD5_KEY = #{md5Key}

select count(0)
from ${dataLogRequest.tableName}
where P_MD5_KEY = #{dataLogRequest.md5Key}
and COUNTER_UPLOAD_TIME between #{dataLogRequest.startTime} and #{dataLogRequest.endTime}

    and ACTIVE = #{dataLogRequest.active}

select

from ${dataLogRequest.tableName}
where DEVICE_UPLOAD_TIME between #{dataLogRequest.startTime} and #{dataLogRequest.endTime}

select

from ${dataLogRequest.tableName}
where COUNTER_UPLOAD_TIME between #{dataLogRequest.startTime} and #{dataLogRequest.endTime}


        and P_MD5_KEY = #{dataLogRequest.md5Key}


        and MD5_KEY = #{dataLogRequest.md5Key}



    and ACTIVE = #{dataLogRequest.active}
