mapper.insert:
SELECT SEQ_DEVICE_PROCESS_LOG.CURRVAL FROM DUAL
insert into ${tableName} (
) values ( SEQ_DEVICE_PROCESS_LOG.NEXTVAL, #{imei,jdbcType=VARCHAR}, #{ua,jdbcType=VARCHAR}, #{modelName,jdbcType=VARCHAR}, #{channelId,jdbcType=VARCHAR}, #{deviceCode,jdbcType=VARCHAR}, #{groupId,jdbcType=BIGINT}, #{batchCode,jdbcType=VARCHAR}, #{processTime,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP} )

mapper.select:
select


from (
        select t2.*,rownum rn from ${tableName} t2
                 where t2.create_time >= #{startDate,jdbcType=TIMESTAMP}
                    and t2.create_time< #{endDate,jdbcType=TIMESTAMP}
) t where rn> ${startNum} and rn <= ${endNum}

select ci.imei,ci.model_name,ci.process_time from
${tableName} ci where 1=1

    and ci.GROUP_ID = #{groupId}


    and ci.channel_id = #{channelId}


    and ci.device_code = #{deviceCode}


    and ci.model_name = #{modelName}


    and ci.process_time = #{processTime}

ORDER BY ci.process_id DESC
