mapper.select:
select * from

where 1=1

    and exists (
    select info.PRODUCT_ID from TY_PRODUCT_INFO info
    where PARTNER_ID = #{record.partnerId}
    and info.QUERY_DATA_SOURCE= 'Y'
    and info.PRODUCT_ID = PRODUCT_ID
    )


    and CHANNEL_ID = #{channelId}



    and GROUP_ID = #{record.groupId}



    and PRODUCT_ID = #{record.productId}


    and UA = #{record.ua}


    and STAT_DATE >= #{record.startDate}



    and STAT_DATE <= #{record.endDate}


order by STAT_DATE desc

select
PRODUCT_ID,
GROUP_ID,
CHANNEL_ID,
UA,
PROCESS_DATE,
sum(stat.TOTAL_NUM) TOTAL_NUM,
sum(stat.VALID_NUM) VALID_NUM
from

where 1=1

    and exists (
    select info.PRODUCT_ID from TY_PRODUCT_INFO info
    where PARTNER_ID = #{partnerId}
    and info.QUERY_DATA_SOURCE= 'Y'
    and info.PRODUCT_ID = PRODUCT_ID
    )


    and CHANNEL_ID = #{record.channelId}



    and GROUP_ID = #{groupId}



    and PRODUCT_ID = #{record.productId}


    and UA = #{record.ua}


    and STAT_DATE >= #{record.startDate}



    and STAT_DATE <= #{record.endDate}


group by PRODUCT_ID,GROUP_ID,CHANNEL_ID,UA,STAT_DATE
order by STAT_DATE desc

select
sum(stat.TOTAL_NUM) TOTAL_NUM,
sum(stat.VALID_NUM) VALID_NUM
from

where 1=1

    and exists (
    select info.PRODUCT_ID from TY_PRODUCT_INFO info
    where PARTNER_ID = #{partnerId}
    and info.QUERY_DATA_SOURCE= 'Y'
    and info.PRODUCT_ID = PRODUCT_ID
    )


    and CHANNEL_ID = #{channelId}


    and PRODUCT_ID = #{productId}


    and UA = #{ua}


    and STAT_DATE >= #{startDate}



    and STAT_DATE <= #{endDate}
